import java.util.Scanner;
import java.util.InputMismatchException;
import java.util.NoSuchElementException;

public class MathOperations {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        if (args.length > 0) {
            // If ARG is passed, use it as input
            String arg = args[0];
            System.out.println("Received input: " + arg);

            // Example: Assume ARG is a number range (e.g., "5 10")
            String[] parts = arg.split(" ");
            if (parts.length == 2) {
                int start = Integer.parseInt(parts[0]);
                int end = Integer.parseInt(parts[1]);

                System.out.println("Displaying prime numbers between " + start + " and " + end);
                displayPrimes(start, end);
            } else {
                System.out.println("Invalid range format. Use: 'start end'");
            }
        } else {
            // If no ARG is passed, proceed with manual inputs
            System.out.print("Enter two numbers to multiply: ");
            int num1 = scanner.nextInt();
            int num2 = scanner.nextInt();
            System.out.println("Multiplication result: " + multiply(num1, num2));

            System.out.print("Enter two numbers to divide (numerator and denominator): ");
            int numerator = scanner.nextInt();
            int denominator = scanner.nextInt();
            System.out.println("Division result: " + divide(numerator, denominator));

            System.out.print("Enter two numbers to subtract (minuend and subtrahend): ");
            int minuend = scanner.nextInt();
            int subtrahend = scanner.nextInt();
            System.out.println("Subtraction result: " + subtract(minuend, subtrahend));

            System.out.print("Enter the range to display prime numbers (start and end): ");
            int start = scanner.nextInt();
            int end = scanner.nextInt();
            displayPrimes(start, end);
        }

        scanner.close();
    }

    public static int multiply(int a, int b) {
        return a * b;
    }

    public static double divide(int a, int b) {
        if (b == 0) {
            System.out.println("Division by zero is not allowed.");
            return 0;
        }
        return (double) a / b;
    }

    public static int subtract(int a, int b) {
        return a - b;
    }

    public static void displayPrimes(int start, int end) {
        System.out.println("Prime numbers between " + start + " and " + end + ":");
        for (int i = start; i <= end; i++) {
            if (isPrime(i)) {
                System.out.print(i + " ");
            }
        }
        System.out.println();
    }

    public static boolean isPrime(int num) {
        if (num <= 1) return false;
        for (int i = 2; i <= Math.sqrt(num); i++) {
            if (num % i == 0) return false;
        }
        return true;
    }
}





public class ShapePrinter {

    public static void main(String[] args) {
        System.out.println("Printing a Rectangle:");
        printRectangle(5, 10);

        System.out.println("\nPrinting a Triangle:");
        printTriangle(5);
    }

    // Method to print a rectangle
    public static void printRectangle(int height, int width) {
        for (int i = 0; i < height; i++) {
            for (int j = 0; j < width; j++) {
                System.out.print("*");
            }
            System.out.println();
        }
    }

    // Method to print a triangle
    public static void printTriangle(int height) {
        for (int i = 1; i <= height; i++) {
            for (int j = 0; j < i; j++) {
                System.out.print("*");
            }
            System.out.println();
        }
    }
}